import '@testing-library/jest-dom';
import '@testing-library/jest-dom/extend-expect';

import { fireEvent, screen } from '@testing-library/dom';

import * as eddlUtil from '../../../core/analytics/eddl-util';

import pageOptionsInit from '../cgdp-page-options';

jest.mock('../../../core/analytics/eddl-util');

describe('usa-banner', () => {
	afterEach(() => {
		// Hack to clean out the dom.
		document.getElementsByTagName('body')[0].innerHTML = '';
		jest.resetAllMocks();
	});

	it('sending the correct analytics for print on the bottom of body', async () => {
		const pageOption = `
		<div class="cgdp-page-options cgdp-page-options--bottom" aria-label="Page options">
        <button class="usa-button usa-button--unstyled" onclick="window.print()"><svg class="usa-icon " role="img" aria-hidden="true"><use href="data:image/svg+xml;base64,#print"></use></svg></button>
        <a class="usa-button usa-button--unstyled" href="mailto:xyz@abc.com"><svg class="usa-icon " role="img" aria-hidden="true"><use href="data:image/svg+xml;base64,#envelope"></use></svg></a>
      </div>
		`;

		// Lets make a spy to ensure that trackOther is called correctly
		const spy = jest.spyOn(eddlUtil, 'trackOther');

		// Inject the HTML into the dom.
		document.body.insertAdjacentHTML('beforeend', pageOption);

		// Create the banner JS
		pageOptionsInit();

		const print = screen.getByRole('button');

		fireEvent.click(print);

		expect(spy).toHaveBeenCalledWith(
			'PageOptions:LinkClick',
			'PageOptions:LinkClick',
			{
				linkText: 'Print',
				location: 'BodyBottom',
			}
		);
	});

	it('sending the correct analytics for print on the top of body', async () => {
		const pageOption = `
		<div class="cgdp-page-options cgdp-page-options--top" aria-label="Page options">
        <button class="usa-button usa-button--unstyled" onclick="window.print()"><svg class="usa-icon " role="img" aria-hidden="true"><use href="data:image/svg+xml;base64,#print"></use></svg></button>
        <a class="usa-button usa-button--unstyled" href="mailto:xyz@abc.com"><svg class="usa-icon " role="img" aria-hidden="true"><use href="data:image/svg+xml;base64,#envelope"></use></svg></a>
      </div>
		`;

		// Lets make a spy to ensure that trackOther is called correctly
		const spy = jest.spyOn(eddlUtil, 'trackOther');

		// Inject the HTML into the dom.
		document.body.insertAdjacentHTML('beforeend', pageOption);

		// Create the banner JS
		pageOptionsInit();

		const print = screen.getByRole('button');

		fireEvent.click(print);

		expect(spy).toHaveBeenCalledWith(
			'PageOptions:LinkClick',
			'PageOptions:LinkClick',
			{
				linkText: 'Print',
				location: 'BodyTop',
			}
		);
	});

	it('sending the correct analytics for email on the top of body', async () => {
		const pageOption = `
		<div class="cgdp-page-options cgdp-page-options--top" aria-label="Page options">
        <button class="usa-button usa-button--unstyled" onclick="window.print()"><svg class="usa-icon " role="img" aria-hidden="true"><use href="data:image/svg+xml;base64,#print"></use></svg></button>
        <a class="usa-button usa-button--unstyled" href="mailto:xyz@abc.com"><svg class="usa-icon " role="img" aria-hidden="true"><use href="data:image/svg+xml;base64,#envelope"></use></svg></a>
      </div>
		`;

		// Lets make a spy to ensure that trackOther is called correctly
		const spy = jest.spyOn(eddlUtil, 'trackOther');

		// Inject the HTML into the dom.
		document.body.insertAdjacentHTML('beforeend', pageOption);

		// Create the banner JS
		pageOptionsInit();

		const print = screen.getByRole('link');

		fireEvent.click(print);

		expect(spy).toHaveBeenCalledWith(
			'PageOptions:LinkClick',
			'PageOptions:LinkClick',
			{
				linkText: 'Email',
				location: 'BodyTop',
			}
		);
	});

	it('sending the correct analytics for email on the bottom of body', async () => {
		const pageOption = `
		<div class="cgdp-page-options cgdp-page-options--bottom" aria-label="Page options">
        <button class="usa-button usa-button--unstyled" onclick="window.print()"><svg class="usa-icon " role="img" aria-hidden="true"><use href="data:image/svg+xml;base64,#print"></use></svg></button>
        <a class="usa-button usa-button--unstyled" href="mailto:xyz@abc.com"><svg class="usa-icon " role="img" aria-hidden="true"><use href="data:image/svg+xml;base64,#envelope"></use></svg></a>
      </div>
		`;

		// Lets make a spy to ensure that trackOther is called correctly
		const spy = jest.spyOn(eddlUtil, 'trackOther');

		// Inject the HTML into the dom.
		document.body.insertAdjacentHTML('beforeend', pageOption);

		// Create the banner JS
		pageOptionsInit();

		const print = screen.getByRole('link');

		fireEvent.click(print);

		expect(spy).toHaveBeenCalledWith(
			'PageOptions:LinkClick',
			'PageOptions:LinkClick',
			{
				linkText: 'Email',
				location: 'BodyBottom',
			}
		);
	});

	it('does not blow up if there is no toggle', async () => {
		const pageOption = `<div>Is this working?</div>`;

		// Lets make a spy to ensure that trackOther is called correctly
		const spy = jest.spyOn(eddlUtil, 'trackOther');

		// Inject the HTML into the dom.
		document.body.insertAdjacentHTML('beforeend', pageOption);

		// Create the banner JS
		pageOptionsInit();

		expect(spy).not.toHaveBeenCalled();
	});
});
